import { useState } from 'react';
import './BoxScores.css'
import BoxScoreItem from './BoxScoreItem';
import Card from '../UI/Card';
import BoxScoresFilter from './BoxScoresFilter';

const BoxScores = (props) => {

  const [selectedYear, setSelectedYear] = useState('2020');

  const yearChangeHandler = (enteredYear) => {
    setSelectedYear(enteredYear);
  }

  // console.log(props.items[0]);
  return (
    <div>
      <Card className='boxscores'>
        <BoxScoresFilter onYearChange={yearChangeHandler} />
        <BoxScoreItem
          date={props.items[0].date}
          opponent={props.items[0].opponent}
          team={props.items[0].team}
          player={props.items[0].player}
          points={props.items[0].points}
          rebounds={props.items[0].rebounds}
          assists={props.items[0].assists}
        />
        <BoxScoreItem
          date={props.items[1].date}
          opponent={props.items[1].opponent}
          team={props.items[1].team}
          player={props.items[1].player}
          points={props.items[1].points}
          rebounds={props.items[1].rebounds}
          assists={props.items[1].assists}
        />
        <BoxScoreItem
          date={props.items[2].date}
          opponent={props.items[2].opponent}
          team={props.items[2].team}
          player={props.items[2].player}
          points={props.items[2].points}
          rebounds={props.items[2].rebounds}
          assists={props.items[2].assists}
        />
        <BoxScoreItem
          date={props.items[3].date}
          opponent={props.items[3].opponent}
          team={props.items[3].team}
          player={props.items[3].player}
          points={props.items[3].points}
          rebounds={props.items[3].rebounds}
          assists={props.items[3].assists}
        />
      </Card>
    </div>
  )




  // date = { itmes[0].date }
  //     opponent={items[0].opponent}
  //     team={boxscores[0].team}
  //     player={boxscores[0].player}
  //     points={boxscores[0].points}
  //     rebounds={boxscores[0].rebounds}
  //     assists={boxscores[0].assists}

}

export default BoxScores
