import './BoxScoreItem.css'

function BoxScoreItem() {
  const boxscoreDate = new Date(2021, 2, 28);
  const boxscoreOpponent = 'Timberwolves';
  const boxscoreTeam = 'Miami Heat';
  const boxscorePlayer = 'Jimmy Butler';
  const boxscorePoints = 29;
  const boxscoreRebounds = 9;
  const boxscoreAssists = 5;

  return (
    <div className="boxscore-item">
      <div>{boxscoreDate.toISOString()}</div>
      <div>{boxscoreOpponent}</div>
      <div className='boxscore-item__description'>
        <h2>{boxscoreTeam}</h2>
        <h2>{boxscorePlayer}</h2>
        <div className='boxscore-item__stats'>{boxscorePoints} Points</div>
        <div className='boxscore-item__stats'>{boxscoreRebounds} Rebounds</div>
        <div className='boxscore-item__stats'>{boxscoreAssists} Assists</div>
      </div>
    </div>
  );
}

export default BoxScoreItem;
