import { useState } from 'react';
import './BoxScores.css'
import BoxScoreItem from './BoxScoreItem';
import Card from '../UI/Card';
import BoxScoresFilter from './BoxScoresFilter';

const BoxScores = (props) => {

  const [selectedYear, setSelectedYear] = useState('2020');

  const yearChangeHandler = (enteredYear) => {
    setSelectedYear(enteredYear);
  }

  const filteredBoxScores = props.items.filter(boxScore => {
    return boxScore.date.getFullYear().toString() === selectedYear;
  });

  const fruits = [ "apple", "banana", "pineapple", "pear" ]
  const fruitCheck = (fruitArray) => {
    return (fruitArray.length > 3 ? console.log("Too many fruit") : console.log("Not enough fruit"))
  }

  fruitCheck(fruits)

  return (
    <div>
      <Card className='boxscores'>
        <BoxScoresFilter
          selected={selectedYear}
          onYearChange={yearChangeHandler}
        />
        {filteredBoxScores.length === 0 && <p>No Box Score Found.</p>}
        {filteredBoxScores.length > 0 &&
          filteredBoxScores.map((boxscore) => (
            <BoxScoreItem
              key={boxscore.id}
              date={boxscore.date}
              player={boxscore.player}
              points={boxscore.points}
              rebounds={boxscore.rebounds}
              assists={boxscore.assists}
            />
          ))}
      </Card>
    </div>
  )




  // date = { itmes[0].date }
  //     opponent={items[0].opponent}
  //     team={boxscores[0].team}
  //     player={boxscores[0].player}
  //     points={boxscores[0].points}
  //     rebounds={boxscores[0].rebounds}
  //     assists={boxscores[0].assists}

}

export default BoxScores
