import React, { useState } from "react";
import './BoxScoreForm.css'

const BoxScoreForm = () => {

  // const [enteredPlayer, setEnteredPlayer] = useState('');
  // const [enteredPoints, setEnteredPoints] = useState('');
  // const [enteredRebounds, setEnteredRebounds] = useState('');
  // const [enteredAssists, setEnteredAssists] = useState('');
  // const [enteredDate, setEnteredDate] = useState('');
  const [userInput, setUserInput] = useState({
    enteredPlayer: '',
    enteredPoints: '',
    enteredRebounds: '',
    enteredAssists: '',
    enteredDate: ''
  });

  const playerChangeHandler = (event) => {
    setUserInput({
      ...userInput,
      enteredPlayer: event.target.value,
    })
  };

  const pointsChangeHandler = (event) => {
    setUserInput({
      ...userInput,
      enteredPoints: event.target.value,
    })
  };

  const reboundsChangeHandler = (event) => {
    setEnteredRebounds(event.target.value);
  };

  const assistsChangeHandler = (event) => {
    setEnteredAssists(event.target.value);
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
  };

  return <form>
    <div className="new-boxscore__controls">
      <div className="new-boxscore__control">
        <label>Player</label>
        <input type='text' onChange={playerChangeHandler} />
      </div>
      <div className="new-boxscore__control">
        <label>Points</label>
        <input type='number' min='0' step="0" onChange={pointsChangeHandler} />
      </div>
      <div className="new-boxscore__control">
        <label>Rebounds</label>
        <input type='number' min='0' step="0" onChange={reboundsChangeHandler} />
      </div>
      <div className="new-boxscore__control">
        <label>Assists</label>
        <input type='number' min='0' step="0" onChange={assistsChangeHandler} />
      </div>
      <div className="new-boxscore__control">
        <label>Date</label>
        <input type='date' min="1920-01-01" max="2023-12-31" onChange={dateChangeHandler} />
      </div>
    </div>
    <div className="new-boxscore__actions">
      <button type="submit">Add Box Score</button>
    </div>
  </form>
};

export default BoxScoreForm
