import React, {useState} from "react";
import './ExpenseForm.css';


const ExpenseForm = () => {
  const [enteredTitle, setEnteredTitle] = useState('');
  const [enteredAmount, setEnteredAmount] = useState('');
  const [enteredDate, setEnteredDate] = useState('');

  // below is object created in order to use one state for the form

  // const [userInput, setUserInput] = useState({
  //   enteredTitle: '',
  //   enteredAmount: '',
  //   enteredDate: ''
  // });

  const titleChangeHandler = (event) => {
      setEnteredTitle(event.target.value)
      // setUserInput({
      //   // the ...prevState takes an object, takes out the key value pairs and adds them to a new object
      //   // This is so the enteredAmount and enteredDate do not get overwritten

      // setUserInput((prevState) => {
      //   return { ...prevState, enteredTitle: event.target.value };
      // });
  };

  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value)
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value)
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate)
    };
    console.log(expenseData);
    setEnteredTitle('');
  };

  return (
  <form onSubmit={submitHandler}>
    <div className="new-expense__controls">
      <div className="new-expense__control">
        <label>Title</label>
        {/* value={enteredTitle} is called two way binding. It clears the input after the submit button is clicked.  */}
        <input
          type="text"
          value={enteredTitle}
          onChange={titleChangeHandler}
        />
      </div>
      <div className="new-expense__control">
        <label>Amount</label>
          <input
            type="number"
            value={enteredAmount}
            min="0.01" step="0.01"
            onChange={amountChangeHandler}
          />
      </div>
      <div className="new-expense__control">
        <label>Date</label>
          <input type="date" min="2019-01-01" max="2022-12-31" onChange={dateChangeHandler} />
      </div>
    </div>
    <div className="new-expense__actions">
      <button type="submit">Add Expense</button>
    </div>
  </form>
  )
};

export default ExpenseForm
