  // console.log(props.drinks)

  const dataCTX = useContext(DataContext);

  console.log(dataCTX)


  const addToCartHandler = amount => {
    dataCTX.addItem({
      id: props.id,
      name: props.name,
      amount: amount,
      price: props.price
    });
  };


  return (
        <li className={classes.drink}>
          <div>
          <h3>{props.name}</h3>
          <div className={classes.description}>{props.description}</div>
          <div className={classes.price}>${drink.price.toFixed(2)}</div>
          </div>
        <div>
          <DrinkItemForm onAddToCart={addToCartHandler}></DrinkItemForm>
        </div>
        </li>
    )
};

export default DrinkItem
