import React, { useReducer } from "react";
import DataContext from "./data-context";


const defaultDataState = {
  items: [],
  totalAmount: 0
};

const dataReducer = (state, action) => {
  if (action.type==='ADD_DATA') {
    const updatedTotalAmount = state.totalAmount + action.item.price * action.item.amount;
    const existingCartItemIndex = state.items;
    const updatedData = state.items.concat(action.item);

    return {
      items: updatedData,
      totalAmount: updatedTotalAmount
    }
  }
  return defaultDataState
}

const DataProvider = (props) => {

  const [dataState, dispatchDataAction] = useReducer(dataReducer, defaultDataState)

  const addItemToCartHandler = (item) => {
    dispatchDataAction({
      type: 'ADD_DATA',
      item: item
    });
  };

  const removeItemFromCartHandler = (id) => {
    dispatchDataAction({
      type: 'REMOVE_DATA',
      id: id
    })

  };

  const dataContext = {
    items: dataState.items,
    totalAmount: dataState.totalAmount,
    addItem: addItemToCartHandler,
    removeItem: removeItemFromCartHandler
  }

  return (
    <DataContext.Provider value={dataContext}>
      {props.children}
    </DataContext.Provider>
  )
};

export default DataProvider
