// import Card from '../../UI/Card'
import classes from './DrinkItem.module.css'

const DrinkItem = (props) => {
  return (
    props.drinks.map(drink => (
        <li>
          <h3>{drink.name}</h3>
          <div className={classes.description}>{drink.description}</div>
          <div className={classes.price}>${drink.price.toFixed(2)}</div>
        <div></div>
        </li>
    )
    )
  );
};

export default DrinkItem
