import Card from '../../UI/Card'
import classes from './DrinkItem.module.css'

const DrinkItem = (props) => {

  return (
    props.drinks.map(drink => (

      <Card className={classes.drink}>
        <div key={drink.id}>
          <h3>{drink.name}</h3>
          <p className={classes.description}>{drink.description}</p>
          <p className={classes.price}>$ {drink.price}</p>
        </div>
      </Card>
    )
    )
  );
};

export default DrinkItem
