import Card from '../UI/Card'
import classes from './DrinkItem.module.css'

const DrinkItem = (props) => {

  const drinksList = props.drinks.map(drink => (
    <Card>
      <div key={drink.id}>
      <h3>{drink.name}</h3>
      <p>{drink.description}</p>
      <p>{drink.price}</p>
      </div>

    </Card>

    )
    );

    return (
      <Card className={classes.drink}>
        <h1>{drink.name}</h1>
      </Card>
    )
  }


export default DrinkItem
