import React from 'react';

import Movie from './Movie';
import classes from './MoviesList.module.css';

const MovieList = (props) => {

  // const APIKey = '01d8e535061fb74a1d8e7c9695a4ef43'

  function fetchMovieHandler() {
    fetch('https://api.themoviedb.org/3/search/movie?api_key=01d8e535061fb74a1d8e7c9695a4ef43&language=en-US&query=Blade%20Runner&page=1&include_adult=false').then(response => {
      return response.json();
    }).then();
  };

  return (
    <ul className={classes['movies-list']}>
      {props.movies.map((movie) => (
        <Movie
          key={movie.id}
          title={movie.title}
          releaseDate={movie.releaseDate}
          openingText={movie.openingText}
        />
      ))}
    </ul>
  );
};



export default MovieList;
