import React from 'react';

import classes from './Movie.module.css';

const Movie = (props) => {
  const baseURL = ''
  return (
    <li className={classes.movie}>
      <h2>{props.title}</h2>
      <h3>{props.releaseDate}</h3>
      <p>{props.overview}</p>
      <img src={`${baseURL}${props.poster}`} alt="Movie Poster" />
    </li>
  );
};

export default Movie;
