import classes from './MovieForm.module.css';

const MovieForm = (props) => {

  const changeHandler = (event) => {
    const title  = event.target.value
    props.onTitleChange(title)
  }

  const submitHandler = (event) => {
    event.preventDefault();
    const movieTitle = event.target.elements.movieTitle.value
    props.onMovieTitleSubmit(movieTitle)
    props.onSubmit(movieTitle)
    };

  // const changeHandler = (event) => {
  //   setSearch(event.target.value)
  // }

  return (
    <div>
      <form className={classes.form} onSubmit={submitHandler}>
        <input name='movieTitle' type='text' onChange={changeHandler}></input>
        <button  onClick={props.fetchMovieHandler}>Fetch Movies</button>
      </form>
    </div>
  )

}

export default MovieForm;
