import React, {useState} from 'react';
import MoviesList from './components/MoviesList';
import './App.css';
import MovieForm from './components/MovieForm';

function App() {
  const [movies, setMovies] = useState([]);

  const APIKey = '01d8e535061fb74a1d8e7c9695a4ef43'

  function fetchMovieHandler() {
    fetch(`https://api.themoviedb.org/3/search/movie?api_key=${APIKey}&language=en-US&query=Blade%20Runner&page=1&include_adult=false`).then(response => {
      return response.json();
    }).then(data => {
      const transformedMovies = data.results.splice(0, 4).map(movieData => {
        return {
          id: movieData.id,
          title: movieData.title,
          releaseDate: movieData.release_date,
          overview: movieData.overview
        }
      })
      setMovies(transformedMovies);
    });
  };


  return (
    <React.Fragment>
      <section>
        <MovieForm submitHandler={fetchMovieHandler}/>
        {/* <button onClick={fetchMovieHandler}>Fetch Movies</button> */}
      </section>
      <section>
        <MoviesList movies={movies} />
      </section>
    </React.Fragment>
  );
}

export default App;
