import ProductItem from './ProductItem';
import classes from './Products.module.css';

const Products = (props) => {

  const DUMMY_PRODUCTS = [
    {
      id: 1,
      title: "Wireless Bluetooth Headphones",
      price: 89.99,
      description: "Experience high-quality sound with our wireless Bluetooth headphones. With a sleek and comfortable design, these headphones are perfect for music lovers on the go."
    },
    {
      id: 2,
      title: "Smartwatch with Heart Rate Monitor",
      price: 129.99,
      description: "Stay on top of your fitness goals with our smartwatch. Featuring a built-in heart rate monitor, step counter, and GPS tracking, this watch is perfect for anyone who wants to stay active and healthy."
    },
    {
      id: 3,
      title: "Gaming Laptop with NVIDIA Graphics",
      price: 1499.99,
      description: "Get the ultimate gaming experience with our high-performance gaming laptop. With NVIDIA graphics and a lightning-fast processor, this laptop can handle even the most demanding games."
    },
    {
      id: 4,
      title: "Professional DSLR Camera",
      price: 1999.99,
      description: "Capture stunning photos and videos with our professional DSLR camera. With a high-resolution sensor and advanced autofocus system, this camera is perfect for photographers who demand the best."
    },
  ];


  return (
    <section className={classes.products}>
      <h2>Buy your favorite products</h2>
      <ul>
        {DUMMY_PRODUCTS.map((product) => (
        <ProductItem
          id={product.id}
          title={product.title}
          price={product.price}
          description='This is a first product - amazing!'
        />
        ))}
      </ul>
    </section>
  );
};

export default Products;
