import { cartActions } from '../../store/cart-slice';
import Card from '../UI/Card';
import classes from './ProductItem.module.css';
import { useDispatch, useSelector } from 'react-redux';

const ProductItem = (props) => {
  const cart = useSelector((state)=> state.cart);
  const dispatch = useDispatch();
  const { title, price, description, id } = props;

  const addItemToCartHandler = () => {
    const newTotalQuantity = cart.totalQuantity + 1;
    const updatedItems = cart.items.slice(); // creates a copy because of mutation
    const existingItem = updatedItems.find((item) => item.id === id);
    if (existingItem) {
      const updatedItem = {...existingItem}; // new object + copy existing properties
      updatedItem.quantity++;
      updatedItem.price = updatedItem.price + price;
      const existingItemIndex = updatedItems.find((item) => item.id === id);
      updatedItems[existingItemIndex] = updatedItem;
    } else {
      updatedItems.push({
        id: id,
        price: price,
        quantity: 1,
        totalPrice: price,
        name: title,
      });
    }

    dispatch(cartActions.increment({
      id,
      title,
      price,
    })
    );
  }

  return (
    <li className={classes.item}>
      <Card>
        <header>
          <h3>{title}</h3>
          <div className={classes.price}>${price.toFixed(2)}</div>
        </header>
        <p>{description}</p>
        <div className={classes.actions}>
          <button onClick={addItemToCartHandler}>Add to Cart</button>
        </div>
      </Card>
    </li>
  );
};

export default ProductItem;
